  /**/
var loadDisqus = (disqusShortname) => {
  var disqusTarget = document.getElementById('disqus_thread');
  var loader = document.getElementById('disqus_loader');

  var disqusEmbed = document.createElement('script');
  var disqusHook = document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0];

  var observer = new IntersectionObserver((entries) => {
    entries.forEach((entry) => {
if (entry.isIntersecting) {  
disqusEmbed.type = 'text/javascript';
disqusEmbed.async = true;
disqusEmbed.src = `https://${disqusShortname}.disqus.com/embed.js`;
 disqusHook.appendChild(disqusEmbed);
 observer.disconnect();
      }
    });
  }, {
    rootMargin: '0px',
    threshold:0.1,
  });

if (disqusTarget) {
observer.observe(disqusTarget);
  }
};
window.addEventListener('load', () =>{
        var canonicalUrl = '<data:blog.canonicalUrl/>';
