if (cat.term === 'New' || cat.term === 'Member' || cat.term === 'User') {
 document.write('<a title="' + title + '" rel="nofollow" class="labelnewchp" href="/search/label/'+ encodeURIComponent(cat.term) +'"><span class="fs-12 ml-5 user_vip"></span></a>');break;}}}

   if (showpostdate) {
