function timeAgo(o){var t=(new Date).getTime()-o.getTime(),e=Math.floor(t/1e3),r=Math.floor(e/60),a=Math.floor(r/60),g=Math.floor(a/24),h=Math.floor(g/30),n=Math.floor(h/12);return 0===t?" baru saja":e<60?e+" detik lalu":r<60?r+" menit lalu":a<24?a+" jam lalu":g<30?g+" hari lalu":h<12?h+" bulan lalu":n+" tahun lalu"}
