function NFrtBookmark(nZeroBook) {  if (nZeroBook === 0) {    return 0;  } else if (nZeroBook >= 1000000000) {    const formatted = (nZeroBook / 1000000000).toFixed(1);    return formatted.endsWith('.0') ? formatted.slice(0, -2) + 'M' : formatted + 'M';  } else if (nZeroBook >= 1000000) {    const formatted = (nZeroBook / 1000000).toFixed(1);    return formatted.endsWith('.0') ? formatted.slice(0, -2) + 'JT' : formatted + 'JT';  } else if (nZeroBook >= 1000) {    const formatted = (nZeroBook / 1000).toFixed(1);    return formatted.endsWith('.0') ? formatted.slice(0, -2) + 'K' : formatted + 'K';  } else {    return nZeroBook;  }}
