const limitHistory = 6;
const histories = (function () {
    list = [];

    function Item(id, title, link) {
        this.id = id;
        this.title = title;
        this.link = link;
    }

    function setHistory() {
        localStorage.setItem('History', JSON.stringify(list));
    }

    function loadHistory() {
        list = JSON.parse(localStorage.getItem('History'));
    }

    if (localStorage.getItem("History") != null) {
        loadHistory();
    }

    obj = {};
    obj.addHistory = function (id, title, link) {
        var item = new Item(id, title, link);
        if (list != null) {
            same = list.find(item => {
                return item.id == id;
            });
            if (list.length < limitHistory) {
                if (!same) {
                    list.push(item);
                    setHistory();
                } else {
                    for (let item in list) {
                        if (list[item].id === id) {
                            list.splice(list.length, 0, list.splice(item, 1)[0]);
                        }
                        setHistory();
                    }
                }
            } else {
                if (!same) {
                    list.splice(0, 1);
                    list.push(item);
                    setHistory();
                } else {
                    for (let item in list) {
                        if (list[item].id === id) {
                            list.splice(list.length, 0, list.splice(item, 1)[0]);
                        }
                        setHistory();
                    }
                }
            }
        } else {
            list.push(item);
            setHistory();
        }
    }

    obj.addHistory = function (id, title, link, coverSCH) {
var item = new Item(id, title, link);
item.coverSCH = coverSCH; 
   if (list != null) {
same = list.find(item => {
return item.id == id;
            });
if (list.length < limitHistory) {
if (!same) {
list.push(item);
setHistory();
 } else {

for (let item in list) {
if (list[item].id === id) {
list.splice(list.length, 0, list.splice(item, 1)[0]);
   }
   setHistory();
   }
  }
} else {
if (!same) {
list.splice(0, 1);
list.push(item);
setHistory();
  } else {
for (let item in list) {

if (list[item].id === id) {
list.splice(list.length, 0, list.splice(item, 1)[0]);
     }
setHistory();
     }
    }
   }
  } else {
 list.push(item);
 setHistory();
        }
    }
return obj;
})();
