  //
  /* numpage2 boxchp */
$(document).ready(function() {
  var perPage = perPageLatestupdate;
  var maxPagesToShow = 3;
  var $boxchp = $('.boxchp');
  var $numpage = $('.numpage');

  function showPage(pageNum) {
 if (pageNum !== 1) {
    var newUrl = window.location.href.split('?')[0];
    window.history.pushState({ path: newUrl }, '', newUrl);
  }
    $boxchp.hide().slice((pageNum - 1) * perPage, pageNum * perPage).show();
    $numpage.find('.page').removeClass('active');
    $numpage.find('.page[data-page="' + pageNum + '"]').addClass('active');
 }
  var urlParams = new URLSearchParams(window.location.search);
  var currentPage = parseInt(urlParams.get('page')) || 1;

  var numPages = Math.ceil($boxchp.length / perPage);

function updatePagination() {
    var startPage = 1;
    var endPage = numPages;
    if (numPages > maxPagesToShow) {
      var middlePage = Math.floor(maxPagesToShow / 2);
      startPage = currentPage - middlePage;
      endPage = currentPage + middlePage;
      if (startPage <= 0) {
        endPage += Math.abs(startPage) + 1;
        startPage = 1;
      }
      if (endPage > numPages) {
        startPage -= (endPage - numPages);
        endPage = numPages;
      }
    }
    var pagesHtml = '';
    if (startPage > 1) {
      pagesHtml += '<a class="page" href="#" data-page="1">1</a>';
      if (startPage > 2) {
        pagesHtml += '<span class="ellipsis">...</span>';
      }
    }
    for (var i = startPage; i <= endPage; i++) {
      var activeClass = (i === currentPage) ? 'active' : '';
      pagesHtml += '<a class="page ' + activeClass + '" href="#" data-page="' + i + '">' + i + '</a>';
    }
    if (endPage < numPages) {
      if (endPage < numPages - 1) {
        pagesHtml += '<span class="ellipsis">...</span>';
      }
      pagesHtml += '<a class="page" href="#" data-page="' + numPages + '">' + numPages + '</a>';
    }
    $('.pages-container').html(pagesHtml);
  }
  $numpage.on('click', '.page', function(e) {
    e.preventDefault();
    var pageNum = parseInt($(this).data('page'));
    currentPage = pageNum;
    showPage(pageNum);
    updatePagination();
  });
  $('.next').on('click', function(e) {
    e.preventDefault();
    if (currentPage < numPages) {
      currentPage++;
      showPage(currentPage);
      updatePagination();
    }
  });
  $('.prev').on('click', function(e) {
    e.preventDefault();
    if (currentPage > 1) {
      currentPage--;
      showPage(currentPage);
      updatePagination();
    }
  });
  showPage(currentPage);
  updatePagination();
});
  //


